Smart::StatusCode ComponentB::connectB_In(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = b_In->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->b_In->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	b_In->subscribe(connections.b_In.interval);
	return status;
}


/**
 * First connect ALL client ports contained in this component, then start all services:
 * activate state, push, etc...
 */
Smart::StatusCode ComponentB::connectAndStartAllServices() {
	Smart::StatusCode status = Smart::SMART_OK;
	
	status = connectB_In(connections.b_In.serverName, connections.b_In.serviceName);
	if(status != Smart::SMART_OK) return status;
	return status;
}

/**
 * Start all tasks contained in this component.
 */
void ComponentB::startAllTasks() {
	// start task Activity1
	if(connections.activity1.scheduler != "DEFAULT") {
		ACE_Sched_Params activity1_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.activity1.scheduler == "FIFO") {
			activity1_SchedParams.policy(ACE_SCHED_FIFO);
			activity1_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.activity1.scheduler == "RR") {
			activity1_SchedParams.policy(ACE_SCHED_RR);
			activity1_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		activity1->start(activity1_SchedParams, connections.activity1.cpuAffinity);
	} else {
		activity1->start();
	}
}

