void ComponentB::loadParameter(int argc, char *argv[])
{
	/*
	 Parameters can be specified via command line --filename=<filename> or -f <filename>

	 With this parameter present:
	   - The component will look for the file in the current working directory,
	     a path relative to the current directory or any absolute path
	   - The component will use the default values if the file cannot be found

	 With this parameter absent:
	   - <Name of Component>.ini will be read from current working directory, if found there
	   - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	   - Default values will be used if neither found in working directory or /etc
	 */
	SmartACE::SmartIniParameter parameter;
	std::ifstream parameterfile;
	bool parameterFileFound = false;

	// load parameters
	try
	{
		// if paramfile is given as argument
		if(parameter.tryAddFileFromArgs(argc,argv,"filename", 'f'))
		{
			parameterFileFound = true;
			std::cout << "parameter file is loaded from an argv argument \n";
		} else if(parameter.searchFile("ComponentB.ini", parameterfile)) {
			parameterFileFound = true;
			std::cout << "load ComponentB.ini parameter file\n";
			parameter.addFile(parameterfile);
		} else {
			std::cout << "WARNING: ComponentB.ini parameter file not found! (using default values or command line arguments)\n";
		}
		
		// add command line arguments to allow overwriting of parameters
		// from file
		parameter.addCommandLineArgs(argc,argv,"component");
		
		// initialize the naming service using the command line parameters parsed in the
		// SmartIniParameter class. The naming service parameters are expected to be in
		// the "component" parameter group.
		SmartACE::NAMING::instance()->checkForHelpArg(argc,argv);
		if(parameterFileFound) 
		{
			if(SmartACE::NAMING::instance()->init(parameter.getAllParametersFromGroup("component")) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		} else {
			if(SmartACE::NAMING::instance()->init(argc, argv) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		}
			
		// print all known parameters
		// parameter.print();
		
		//--- server port // client port // other parameter ---
		// load parameter
		parameter.getString("component", "name", connections.component.name);
		parameter.getString("component", "initialComponentMode", connections.component.initialComponentMode);
		if(parameter.checkIfParameterExists("component", "defaultScheduler")) {
			parameter.getString("component", "defaultScheduler", connections.component.defaultScheduler);
		}
		if(parameter.checkIfParameterExists("component", "useLogger")) {
			parameter.getBoolean("component", "useLogger", connections.component.useLogger);
		}
		
		// load parameters for client B_In
		parameter.getString("B_In", "serviceName", connections.b_In.serviceName);
		parameter.getString("B_In", "serverName", connections.b_In.serverName);
		parameter.getString("B_In", "wiringName", connections.b_In.wiringName);
		parameter.getInteger("B_In", "interval", connections.b_In.interval);
		if(parameter.checkIfParameterExists("B_In", "roboticMiddleware")) {
			parameter.getString("B_In", "roboticMiddleware", connections.b_In.roboticMiddleware);
		}
		
		// load parameters for server B_Out
		parameter.getString("B_Out", "serviceName", connections.b_Out.serviceName);
		if(parameter.checkIfParameterExists("B_Out", "roboticMiddleware")) {
			parameter.getString("B_Out", "roboticMiddleware", connections.b_Out.roboticMiddleware);
		}
		
		// load parameters for task Activity1
		parameter.getDouble("Activity1", "minActFreqHz", connections.activity1.minActFreq);
		parameter.getDouble("Activity1", "maxActFreqHz", connections.activity1.maxActFreq);
		parameter.getString("Activity1", "triggerType", connections.activity1.trigger);
		if(connections.activity1.trigger == "PeriodicTimer") {
			parameter.getDouble("Activity1", "periodicActFreqHz", connections.activity1.periodicActFreq);
		} else if(connections.activity1.trigger == "DataTriggered") {
			parameter.getString("Activity1", "inPortRef", connections.activity1.inPortRef);
			parameter.getInteger("Activity1", "prescale", connections.activity1.prescale);
		}
		if(parameter.checkIfParameterExists("Activity1", "scheduler")) {
			parameter.getString("Activity1", "scheduler", connections.activity1.scheduler);
		}
		if(parameter.checkIfParameterExists("Activity1", "priority")) {
			parameter.getInteger("Activity1", "priority", connections.activity1.priority);
		}
		if(parameter.checkIfParameterExists("Activity1", "cpuAffinity")) {
			parameter.getInteger("Activity1", "cpuAffinity", connections.activity1.cpuAffinity);
		}
		
		// load parameters for PlainOpcUaComponentBExtension
		
		
		// load parameters for all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->loadParameters(parameter);
		}
		
	
	} catch (const SmartACE::IniParameterError & e) {
		std::cerr << e.what() << std::endl;
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std::exception: " << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}
